mapper.select:
select
  rm_id, rm_lift, rm_date, rm_type, rm_user, rm_login, rm_login_addr, rm_logout, rm_logout_addr, rm_url, rm_description
from db_lms.record_maintenance


        and rm_id = #{rmId}


        and rm_lift = #{rmLift}


        and rm_date = #{rmDate}


        and rm_type = #{rmType}


        and rm_user = #{rmUser}


        and rm_login = #{rmLogin}


        and rm_login_addr = #{rmLoginAddr}


        and rm_logout = #{rmLogout}


        and rm_logout_addr = #{rmLogoutAddr}


        and rm_url = #{rmUrl}


        and rm_description = #{rmDescription}
